package com.zhixing.work.zhixin.network;import retrofit2.Retrofit;import retrofit2.adapter.rxjava.RxJavaCallAdapterFactory;import retrofit2.converter.gson.GsonConverterFactory;/** * Created by lhj on 18/5/8 */public class RetrofitServiceFactory {    private static SpaService mSpaService;    public static SpaService getSpaService() {        if (mSpaService == null) {            Retrofit retrofit = new Retrofit.Builder()                    .baseUrl(RequestConstant.BASE_URL)                    .addConverterFactory(GsonConverterFactory.create())                    .client(OkHttpUtil.getInstance().getClient())                    .addCallAdapterFactory(RxJavaCallAdapterFactory.create())                    .build();            mSpaService = retrofit.create(SpaService.class);        }        return mSpaService;    }    public static synchronized void recreateService() {        mSpaService = null;    }}