package com.zhixing.work.zhixin.network;import com.zhixing.work.zhixin.bean.AuthenticateBody;import com.zhixing.work.zhixin.bean.CertificationBody;import com.zhixing.work.zhixin.bean.EducationBody;import com.zhixing.work.zhixin.bean.UpDateInfoBody;import com.zhixing.work.zhixin.network.response.AuthenticateListResult;import com.zhixing.work.zhixin.network.response.CompanyCertificationStatusResult;import com.zhixing.work.zhixin.network.response.EvaluateResult;import com.zhixing.work.zhixin.network.response.ExpectedJobResult;import com.zhixing.work.zhixin.network.response.JudgeTelephoneUsableResult;import com.zhixing.work.zhixin.network.response.LoginResult;import com.zhixing.work.zhixin.network.response.PersonalCardBasicInfoResult;import com.zhixing.work.zhixin.network.response.RegisterResult;import com.zhixing.work.zhixin.network.response.SmsCodeResult;import com.zhixing.work.zhixin.network.response.StsTokenResult;import com.zhixing.work.zhixin.network.response.SubmitAuthenticateResult;import com.zhixing.work.zhixin.network.response.UpdatePasswordResult;import com.zhixing.work.zhixin.requestbody.ExpectedJobBodyBean;import retrofit2.Call;import retrofit2.http.Body;import retrofit2.http.Field;import retrofit2.http.FormUrlEncoded;import retrofit2.http.GET;import retrofit2.http.PATCH;import retrofit2.http.POST;import retrofit2.http.PUT;import retrofit2.http.Query;/** * */public interface SpaService {    @GET(RequestConstant.GET_OSS)    Call<StsTokenResult> getStsToken();    @GET(RequestConstant.AUTHENTICATES)    Call<AuthenticateListResult> getAuthenticates();    @PUT(RequestConstant.AUTHENTICATES)    Call<SubmitAuthenticateResult> submitAuthenticate(@Body AuthenticateBody body);    @PUT(RequestConstant.AUTHENTICATES)    Call<SubmitAuthenticateResult> submitEducationAuthenticate(@Body EducationBody body);    @PUT(RequestConstant.AUTHENTICATES)    Call<SubmitAuthenticateResult> submitCertificationAuthenticate(@Body CertificationBody body);    @GET(RequestConstant.GET_EVALUATE_INFO)    Call<EvaluateResult> evaluateResult(@Query(RequestConstant.KEY_FRONT_OR_BACK) String id,                                        @Query(RequestConstant.KEY_OPT) String opt);    @PUT(RequestConstant.RESUME_EXPECT_JOB)    Call<ExpectedJobResult> updateExpectedJob(@Query(RequestConstant.KEY_RESUME_ID) String resumeId,                                              @Body ExpectedJobBodyBean[] body);    @GET(RequestConstant.GET_EVALUATE_INFO)    Call<PersonalCardBasicInfoResult> getCardBasicInfo(@Query(RequestConstant.KEY_FRONT_OR_BACK) String id,                                                       @Query(RequestConstant.KEY_OPT) String opt);    @POST(RequestConstant.GO_LOGIN)    @FormUrlEncoded    Call<LoginResult> doLogin(@Field(RequestConstant.KEY_PHONE_NUMBER) String PhoneNum,                              @Field(RequestConstant.KEY_PASSWORD) String PassWord,                              @Field(RequestConstant.KEY_USER_ROLE_ENUM) String UserRoleEnum);    @GET(RequestConstant.GET_REGISTER_CODE)    Call<SmsCodeResult> getVerificationCode(@Query(RequestConstant.KEY_PHONE_NUMBER) String PhoneNum,                                            @Query(RequestConstant.KEY_USER_ROLE_ENUM) String UserRoleEnum,                                            @Query(RequestConstant.KEY_SMS_CODE_TYPE_ENUM) String SmsCodeTypeEnum);    @PATCH(RequestConstant.UPDATE_PASSWORD)    Call<UpdatePasswordResult> upDateUserPassword(@Body UpDateInfoBody body);    @POST(RequestConstant.GO_REGISTER)    @FormUrlEncoded    Call<RegisterResult> userRegister(@Field(RequestConstant.KEY_PHONE_NUMBER) String PhoneNum,                                      @Field(RequestConstant.KEY_PASSWORD) String PassWord,                                      @Field(RequestConstant.KEY_ROLE) String Role,                                      @Field(RequestConstant.KEY_VERIFY_CODE) String VerifyCode);    @GET(RequestConstant.GO_REGISTER)    Call<JudgeTelephoneUsableResult> judgeTelephoneUsable(@Query(RequestConstant.KEY_PHONE_NUMBER) String PhoneNum,                                                          @Query(RequestConstant.KEY_ROLE) String Role);    @GET(RequestConstant.COMPANY_CERTIFICATION_STATUS)    Call<CompanyCertificationStatusResult> getCompanyCertificationStatus();}